package com.example.kotlinyelp.data

import org.junit.Assert
import org.junit.Test

import org.mockito.Mockito

class BusinessRepositoryTest {
    @Test
    fun `Get the correct Business object by index`() {
        val location = Location("Mountain View", "California")
        val b1 = Business("Cupcake", location, "alias 1","data:image/jpeg;base64,")
        val b2 = Business("Donut", location, "alias 2","data:image/jpeg;base64,")
        val businessDao = Mockito.mock<BusinessDao>(BusinessDao::class.java)
        Mockito.`when`(businessDao.getBusiness(0)).thenReturn(b1)
        Mockito.`when`(businessDao.getBusiness(1)).thenReturn(b2)

        val obj = BusinessRepository.getInstance(businessDao)

        Assert.assertNull(obj.getBusiness(-1))
        Assert.assertNotNull(obj.getBusiness(0))
        Assert.assertNotNull(obj.getBusiness(1))
        Assert.assertNull(obj.getBusiness(2))
        Assert.assertEquals("Cupcake", obj.getBusiness(0).name)
        Assert.assertNotEquals("Cupcake", obj.getBusiness(1).name)
        Assert.assertEquals("Donut", obj.getBusiness(1).name)
        Assert.assertEquals("alias 2", obj.getBusiness(1).alias)
    }
}